import React, { useEffect, useState } from "react";

const Pagination = () => {
  const pages = 10;
  const pageNumber = [];
  for (let i = 1; i <= pages; i++) {
    pageNumber.push(i);
  }
  

  // for (let i = 1; i <= Math.ceil(pages / pageCount); i++) {
  //   pageNumber.push(i);
  // }
  const [currentButton, setcurrentButon] = useState(1)
  const [arrOfCurrButtons, setarrOfCurrButtons] = useState([]);

  useEffect(() => {
    let tempNumberOfPages = [...pageNumber];
    if (currentButton >= 1 && currentButton <= 3) {
      tempNumberOfPages = [1,2,3,'...', pageNumber.length]
    }
    else if(currentButton === 4){
      const sliced = pageNumber.slice(0, 5)
      tempNumberOfPages = [...sliced, pageNumber.length]
    }

    else if (currentButton > 4 && currentButton < pageNumber.length - 2) {
      const sliced1 = pageNumber.slice(currentButton - 2, currentButton)
      const sliced2 = pageNumber.slice(currentButton, currentButton + 2);
      tempNumberOfPages - ([1, '...', ...sliced1, sliced2, '...', pageNumber.length])
    }
    setarrOfCurrButtons(tempNumberOfPages)
  }, [currentButton]);
  return (
    <div>
      <div className="pagination-container">
        <a
          className="disable"
          onClick={() =>
            setcurrentButon((prev) => (prev === 1 ? prev : prev - 1))
          }
        >
          Prev
        </a>
        {arrOfCurrButtons.map((page) => {
          return <a className={currentButton === page && 'active'}>{page}</a>;
        })}
        <a
          className="disable"
          onClick={() =>
            setcurrentButon((prev) =>
              prev === pageNumber.length ? prev : prev + 1
            )
          }
        >
          Next
        </a>
      </div>
    </div>
    // <nav aria-label="Page navigation example">
    //   <ul className="pagination">
    //     <li className="page-item">
    //       <a className="page-link" >
    //         Previous
    //       </a>
    //     </li>
    //     {pageNumber.map((page) => {
    //       return (
    //         <li className="page-item" key={page}>
    //           <a className="page-link" on>{page}</a>
    //         </li>
    //       );
    //     })}
    //     <li className="page-item">
    //       <a className="page-link">Next</a>
    //     </li>
    //   </ul>
    // </nav>
  );
};

export default Pagination;

