import React from 'react'

const Pagination = ({ pageCount, totalPost, currentPage }) => {
  const pageNumber = [];
  for (let i = 1; i <= Math.ceil(totalPost / pageCount); i++) {
    pageNumber.push(i);
  }
  return (
    <nav aria-label="Page navigation example">
      <ul className="pagination">
        <li className="page-item">
          <a className="page-link">Previous</a>
        </li>
        {pageNumber.map((page) => {
          return (
            <li
              className="page-item"
              key={page}
              onChange={(e) => pageCount(e.target.value)}
            >
              <a className="page-link" href={currentPage}>
                {page}
              </a>
            </li>
          );
        })}
        <li className="page-item">
          <a className="page-link" href="!#">
            Next
          </a>
        </li>
      </ul>
    </nav>
  );
};

export default Pagination
