import React, { useEffect, useState } from "react";
import axios from "axios";
import _ from "lodash";

const Table = () => {
  useEffect(() => {
    getAllData();
  }, []);

  //menampilkan semua data
  const [datas, getData] = useState([]);
  const getAllData = () => {
    axios
      .get("/api/data")
      .then((response) => {
        const allData = response.data.data_barang;
        getData(allData);
      })
      .catch((error) => console.log(`error: ${error}`));
  };
  //melakukan action pada item search
  const [search, setSearch] = useState("");
  // const [row, setRow] = useState("");

  const pageSize = 10;
  const pageCount = datas ? Math.ceil(datas.length / pageSize) : 0;
  // if (pageCount === 1) return null;
  // const pages = _.range(1, pageCount + 1);

  return (
    <div className="App">
      <div className="container py-4">
        <h1>Reatc Table</h1>
        <h4> data barang</h4>
        <div className="rxid-table">
          <div className="rxid-table-header">
            <div className="input-group ">
              <span className="input-group-text" id="addon-wrapping">
                <em className="fa fa-search"></em>
              </span>
              <input
                type="text"
                className="form-control"
                placeholder="Search ..."
                aria-label="Search ..."
                aria-describedby="addon-wrapping"
                onChange={(e) => setSearch(e.target.value)}
              />
            </div>
            <div className="add-data d-grid ">
              <button type="button" class="btn btn-success">
                <i className="fa fa-plus">
                  <span>Add data</span>
                </i>
              </button>
            </div>
          </div>
          <div className="rxid-table-body">
            <div className="table-responsive">
              <table className="table table-striped">
                {!datas ? (
                  "No data found"
                ) : (
                  <thead>
                    <tr>
                      <th className="sortable">
                        <div className="th-content">
                          <span className="th-text">No</span>
                          <span className="sort">
                            <em className="fa fa-sort" />
                          </span>
                        </div>
                      </th>
                      <th className="sortable">
                        <div className="th-content">
                          <span className="th-text">Nama Barang</span>
                          <span className="sort">
                            <em className="fa fa-sort" />
                          </span>
                        </div>
                      </th>
                      <th className="sortable">
                        <div className="th-content">
                          <span className="th-text">Deskripsi</span>
                          <span className="sort">
                            <em className="fa fa-sort" />
                          </span>
                        </div>
                      </th>
                      <th className="sortable">
                        <div className="th-content">
                          <span className="th-text">Jumlah Barang</span>
                          <span className="sort">
                            <em className="fa fa-sort" />
                          </span>
                        </div>
                      </th>
                      <th className="sortable">
                        <div className="th-content">
                          <span className="th-text">Harga Barang</span>
                          <span className="sort">
                            <em className="fa fa-sort" />
                          </span>
                        </div>
                      </th>
                      <th>Aksi</th>
                    </tr>
                  </thead>
                )}
            
                <tbody>
                  {data
                    .filter((data) =>
                      data.namabarang.toLowerCase().includes(search)
                    )
                    .map((data, _id) => {
                      return (
                        <tr key={_id}>
                          <td>{_id + 1}</td>
                          <td>{data.namabarang}</td>
                          <td>{data.deskripsi}</td>
                          <td>{data.jumlah}</td>
                          <td>{data.harga}</td>
                          <td d-grid gap-2 d-flex>
                            <button className="btn btn-sm btn-warning mx-2">
                              <em className="fa fa-solid fa-pencil"></em>
                            </button>
                            <button className="btn btn-sm btn-danger">
                              <em className="fa fa-solid fa-trash"></em>
                            </button>
                          </td>
                        </tr>
                      );
                    })}
                </tbody>
              </table>
            </div>
          </div>
          <div className="rxid-table-footer">
            <div className="select-max-row">
              <select
                className="form-select form-select-sm"
                aria-label="Default select example"
                value={5}
              >
                <option value={5}>5</option>
                <option value={10}>10</option>
                <option value={15}>15ree</option>
              </select>
            </div>
            {/* <nav aria-label="Page navigation example">
              {pages.map((page) => {
                return (
                  <ul className="pagination">
                    <li className="page-item">
                      <a className="page-link" href="#">
                        Previous
                      </a>
                    </li>
                    <li className="page-item">
                      <a className="page-link">{page}</a>
                    </li>
                    <li className="page-item">
                      <a className="page-link" href="#">
                        Next
                      </a>
                    </li>
                  </ul>
                  // <li className="page-item">
                  //   <a className="page-link">{page}</a>
                  // </li>
                );
              })}
            </nav> */}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Table;
