import React, { useEffect, useState } from "react";

const Pagination = ({ pageCount, totalPost, paginate, setcurrentPage }) => {
  const pageNumber = [];
  for (let i = 1; i <= Math.ceil(totalPost / pageCount); i++) {
    pageNumber.push(i);
  }

  const prevPaginate =((pageNumber)=> setcurrentPage(pageNumber - 1))

  return (
    <nav aria-label="Page navigation example">
      <ul className="pagination">
        <li className="page-item">
          <a className="page-link" onClick={() => prevPaginate(pageNumber)}>
            Previous
          </a>
        </li>
        {pageNumber.map((page) => {
          return (
            <li className="page-item" key={page}>
              <a className="page-link" onClick={() => paginate(page )}>
                {page}
              </a>
            </li>
          );
        })}
        <li className="page-item">
          <a className="page-link">Next</a>
        </li>
      </ul>
    </nav>
  );
};

export default Pagination;
